// Simple short notification sounds (Base64 encoded MP3s)

const NOTIFICATION_SOUND = 'data:audio/mp3;base64,';
const MESSAGE_SOUND = 'data:audio/mp3;base64,';
// Note: Real sounds should be longer/different. Using placeholder silent/short base64 for safety if real assets aren't available. 
// Actually, I'll use a reliable "pop" sound URL or generate a beep with AudioContext to ensure it works without external files.
// But AudioContext is better.

class SoundManager {
    constructor() {
        this.ctx = null;
        this.muted = false;
        
        // Add interaction listener to unlock AudioContext
        const unlock = () => {
            if (this.ctx && this.ctx.state === 'suspended') {
                this.ctx.resume().then(() => {
                    console.log('[Sound] AudioContext unlocked and resumed');
                    window.removeEventListener('click', unlock);
                    window.removeEventListener('touchstart', unlock);
                });
            } else if (!this.ctx) {
                this.init();
            }
        };
        
        if (typeof window !== 'undefined') {
            window.addEventListener('click', unlock);
            window.addEventListener('touchstart', unlock);
        }
    }

    init() {
        if (!this.ctx && typeof window !== 'undefined') {
            this.ctx = new (window.AudioContext || window.webkitAudioContext)();
        }
    }

    setMuted(isMuted) {
        this.muted = isMuted;
    }

    // Modern "Bloop" sound for messages
    playMessage() {
        if (this.muted) return;
        this.init();
        
        if (this.ctx.state === 'suspended') {
            this.ctx.resume();
        }
        
        const now = this.ctx.currentTime;
        const osc = this.ctx.createOscillator();
        const gain = this.ctx.createGain();
        
        osc.connect(gain);
        gain.connect(this.ctx.destination);
        
        osc.type = 'sine';
        osc.frequency.setValueAtTime(400, now);
        osc.frequency.exponentialRampToValueAtTime(600, now + 0.05);
        osc.frequency.exponentialRampToValueAtTime(450, now + 0.15);
        
        gain.gain.setValueAtTime(0, now);
        gain.gain.linearRampToValueAtTime(0.1, now + 0.02);
        gain.gain.exponentialRampToValueAtTime(0.001, now + 0.15);
        
        osc.start(now);
        osc.stop(now + 0.15);
    }

    // Modern "Ping" sound for notifications
    playNotification() {
        if (this.muted) return;
        this.init();
        
        if (this.ctx.state === 'suspended') {
            this.ctx.resume();
        }

        const now = this.ctx.currentTime;
        const osc = this.ctx.createOscillator();
        const gain = this.ctx.createGain();
        
        osc.connect(gain);
        gain.connect(this.ctx.destination);
        
        osc.type = 'sine';
        osc.frequency.setValueAtTime(880, now); 
        osc.frequency.exponentialRampToValueAtTime(880, now + 0.1);
        
        gain.gain.setValueAtTime(0, now);
        gain.gain.linearRampToValueAtTime(0.1, now + 0.01);
        gain.gain.exponentialRampToValueAtTime(0.001, now + 0.5);
        
        osc.start(now);
        osc.stop(now + 0.5);
    }
}

export const soundManager = new SoundManager();
